import React from 'react';
import './hero.css'

export default function Hero() {
  return (
    <section className="hero">
  <div className="hero-content">
    <h1 className="title">Little Lemon</h1>
    <h2 className="subtitle">Chicago</h2>
    <p className="text">Little Lemon is a cozy restaurant located in the heart of Chicago, serving delicious Mediterranean cuisine. Our menu features a variety of mouthwatering dishes made with fresh, locally sourced ingredients. Come and enjoy a romantic dinner, a casual lunch, or a fun weekend brunch with friends and family. We look forward to welcoming you to Little Lemon.</p>
  </div>
  <div className="hero-image">
    <img src="/iNamemages/restauranfood.jpg" alt="Picture" />
  </div>
</section>

  )
}
